INSERT INTO `mu_user_enterprise` (`ent_id`, `ent_user_id`, `ent_name`, `ent_type`, `ent_website`, `ent_business_model`, `ent_tax`, `ent_zipcode`, `ent_introduce`, `ent_location`, `ent_city`, `ent_status`, `ent_chief`, `ent_create_time`, `ent_chief_postion`, `ent_business_scope`, `ent_registered_capital`, `ent_recommend`, `ent_logo`, `ent_image`, `ent_update_time`, `ent_check_by`) VALUES (119, 109, '锦州新华龙钼业股份有限公司', 118, '', 6, NULL, '', '<span style="font-family:arial, 宋体, sans-serif;font-size: 14px; line-height: 24px; text-indent: 30px;">锦州新华龙是国内钼行业产品生产骨干企业之一，国家一类出口免检企业，经过多年的发展，已经成为集采 矿、选矿、冶炼、加工、贸易于一体的专业化公司。公司创建于2003年6月，下辖3个全资子公司和2个控股公司，企业公司资产超10亿元，总部坐落在辽宁沿海经济战略带重点发展区域——国家级锦州经济技术开发区。　 公司连续多年实施技改项目，建成工艺和技术完善成熟的回转窑，完全替代了原有落后的反射炉。同时建成与之匹配的环保项目亚硫酸钠工程，达到了绿色环保要求。企业自主设计新建的现代化钼铁冶炼自动化生产线，其技术装备水平和生产能力居国内领先地位。公司先后从美国、日本引进国际先进水平的焙烧、自动化冶炼、环保等技术和检测设备，严格规范原辅料和产品质检制度，全面通过了质量、环境、职业健康安全管理三体系的认证和监督审核。公司生产的主要产品性能均达到国内先进水平，并销往全国各大钢厂，出口到世界数十个国家和地区，钼铁生产量与销售量稳居中国钼业第一方阵，深加工产品——钼酸铵和高纯氧化钼的产量和质量居行业内前列。公司积极组织实施名牌产品和商标战略，全力打造新华龙品牌，被国家农业部授予 “全面质量管理达标验收合格单位”、辽宁省“诚信示范企业”、“守合同重信用企业”、“民营百强企业”和“名牌产品”、“著名商标”等称号，连续多年成为锦州市“工业十强企业”，是注册地重点骨干纳税企业。</span>', '锦州经济技术开发区', 189, 1, 'jzxhlmy', '2013-03-17 22:34:12', NULL, '钼精矿、钼铁、钼酸铵、高纯氧化钼、钼粉、钼板、钼棒、钼顶头', 10000, NULL, NULL, NULL, NULL, 'ueelife');
INSERT INTO `mu_user_enterprise` (`ent_id`, `ent_user_id`, `ent_name`, `ent_type`, `ent_website`, `ent_business_model`, `ent_tax`, `ent_zipcode`, `ent_introduce`, `ent_location`, `ent_city`, `ent_status`, `ent_chief`, `ent_create_time`, `ent_chief_postion`, `ent_business_scope`, `ent_registered_capital`, `ent_recommend`, `ent_logo`, `ent_image`, `ent_update_time`, `ent_check_by`) VALUES (120, 110, '嵩县开拓者钼业有限公司', 118, '', 7, NULL, '', '<p style="margin: 5px 0px 0px; padding: 0px; border: none; width: 650px; text-indent: 25px; line-height: 24px; color: rgb(109, 109, 109); font-family: 宋体;">洛阳嵩县开拓者钼业有限公司是一家科技型的钼加工企业，主要产品有99.9%<strong>高纯二硫化钼</strong>、99.5%<strong>高纯二硫化钼</strong>、99%<strong>高纯二硫化钼</strong>、98.5%<strong>二硫化钼</strong>、98%<strong>二硫化钼</strong>、<strong>高纯超细三氧化钼</strong>、<strong>炼钢钼粉</strong>、<strong>金属钼粉</strong>等<strong>钼</strong>系列制品。</p><p style="margin: 5px 0px 0px; padding: 0px; border: none; width: 650px; text-indent: 25px; line-height: 24px; color: rgb(109, 109, 109); font-family: 宋体;">公司占地面积148亩，建筑面积33000平方米，按照现代化的花园式工厂设计，厂区依山傍水，紧邻洛栾快速通道，四周群峰迭峦、风景秀丽，距中国最美的地方之一白云山45公里、与国家AAAA级景区天池山遥相呼应。公司现有员工100余人，70％具有大专以上学历，其中硕士1人、博士、博士后各1人，其他专业技术人员近30人。该项目与国家级研究院强强联手，共同研发国际上先进的钼真空冶炼技术及装备，并获得国家发明专利；开创了钼业绿色冶炼的先河，开拓了发展循环经济的新途径，开辟了钼工业可持续发展之路。</p><p style="margin: 5px 0px 0px; padding: 0px; border: none; width: 650px; text-indent: 25px; line-height: 24px; color: rgb(109, 109, 109); font-family: 宋体;">公司以“创新进取、追求卓越”为己任、以“做世界钼真空冶炼先进技术的开拓者”为战略；集科学研究、新产品开发、生产销售为一体，通过了ISO9001质量管理体系、 ISO14001环境管理体系认证、GB/T28001 职业健康安全管理体系认证和瑞奇认证；自主研发与科研院所相结合，依靠科技创新建设绿色环保钼产业链条、从而跨入钼产业多元化发展道路，跻身国内国际市场，把开拓者钼业品牌打造成为闪耀在世界东方的璀璨明珠。</p>', '河南省洛阳市嵩县德亭镇酒店村', 189, 1, 'sxktzmy', '2013-03-17 22:40:19', NULL, '高纯二硫化钼、99.5%高纯二硫化钼、99%高纯二硫化钼、98.5%二硫化钼、98%二硫化钼、高纯超细三氧化钼、炼钢钼粉、金属钼粉', 500, NULL, NULL, NULL, NULL, 'ueelife');
