INSERT INTO `mu_article` VALUES (1202,' 2013年4月14日钼精矿行情 ',NULL,10,16,36,' 2013年4月14日钼精矿行情 ','<p>\r\n	&nbsp;2013年4月14日钼精矿行情较昨天继续走平</p>\r\n',1,'钼价格,钼网站,钼精矿',1,'zgg','2013-04-14 13:56:05',NULL,NULL,NULL),(1203,'钼丝市场走势平缓',NULL,28,17,41,'钼丝市场走势平缓','<p>\r\n	由于目前终端市场需求乏力，钼丝产品的消化过程比较缓慢，成交还是很弱。</p>\r\n',1,'钼丝',114,'robin','2013-04-14 14:47:05','','2013-04-14 14:48:52',NULL),(1206,'钼精矿市场暂时走稳',NULL,15,17,35,'钼精矿市场暂时走稳','<p>\r\n	国内钼精矿市场行走持续走稳，含税钼精矿市场价格继续保持在1550元-1570元/吨。</p>\r\n',1,'钼精矿',114,'zgg','2013-04-14 15:23:35',NULL,NULL,NULL),(1235,'钼精矿技术条件',NULL,7,20,63,'钼精矿技术条件','<p class=\"p0\" style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt;\">\r\n	<span new=\"\" times=\"\">GB&nbsp;3200-89&nbsp;<font face=\"宋体\">代替&nbsp;</font><font face=\"Times New Roman\">GB&nbsp;3200-82</font></span><span new=\"\" times=\"\"><br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; </span><span new=\"\" times=\"\">1&nbsp;<font face=\"宋体\">主题内容与适用范围</font></span><span new=\"\" times=\"\"><br />\r\n	</span><span new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp; 本标准规定了钼精矿的技术要求、试验方法、检验规则和包装、标志、质量证明书。</span><span new=\"\" times=\"\"><br />\r\n	</span><span new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp; 本标准适用于经浮选所得的钼精矿，供生产氧化钼、钼铁、钼盐等用。</span><span new=\"\" times=\"\"><br />\r\n	</span><span new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp; 2&nbsp;<font face=\"宋体\">引用标准</font></span><span new=\"\" times=\"\"><br />\r\n	</span><span new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp; YB&nbsp;735&nbsp;<font face=\"宋体\">钼精矿分析方法</font></span><span new=\"\" times=\"\"><br />\r\n	</span><span new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp; 3&nbsp;<font face=\"宋体\">技术要求</font></span><span new=\"\" times=\"\"><br />\r\n	</span><span new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp; 3.1&nbsp;<font face=\"宋体\">按化学成分不同，钼精矿分成</font><font face=\"Times New Roman\">10</font><font face=\"宋体\">个牌号。以干矿品位计算应符合下表规定。</font></span><span new=\"\" times=\"\"><o:p></o:p></span></p>\r\n<table style=\"padding: 0pt 5.4pt; border-collapse: collapse; mso-table-layout