 INSERT INTO `mu_article` VALUES ('1245','中国建投称原监事长王世强因健康原因去世',NULL,'5','17','41','中国建投称原监事长王世强因健康原因去世','<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<font face=\"Times New Roman\" style=\"font-size: 10.5pt;\">&nbsp;</font><font face=\"宋体\" style=\"font-family: 宋体; font-size: 10.5pt;\">来源：人民网</font><font face=\"Times New Roman\" style=\"font-size: 10.5pt;\">-</font><font face=\"宋体\" style=\"font-family: 宋体; font-size: 10.5pt;\">银行频道 &nbsp; &nbsp;</font></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">&nbsp; &nbsp; &nbsp; 人民网北京<font face=\"Times New Roman\">4</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">18</font><font face=\"宋体\">日电（记者&nbsp;曹华）中国建银投资有限责任公司今日发讣告称，因健康原因，公司原监事长王世强于</font><font face=\"Times New Roman\">2013</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">4</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">17</font><font face=\"宋体\">日在北京去世。而昨日，此事曾在网络及微博上被讹传为中国建投的一名投资经理因黄金下跌暴仓跳楼。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">&nbsp; &nbsp; &nbsp; 据公开资料显示，王世强自<font face=\"Times New Roman\">1977</font><font face=\"宋体\">年至</font><font face=\"Times New Roman\">2003</font><font face=\"宋体\">年期间，曾出任中国人民银行多个职位，历任计划资金司干部、副主任、主任、业务稽核司、副局级稽核员、稽核监督局副局长、银行监管一司副司长等职务，其中</font><font face=\"Times New Roman\">1998</font><font face=\"宋体\">年至</font><font face=\"Times New Roman\">2000</font><font face=\"宋体\">年期间担任国务院稽察特派员总署特派员助理。王世强于</font><font face=\"Times New Roman\">2003</font><font face=\"宋体\">年至</font><font face=\"Times New Roman\">2006</font><font face=\"宋体\">年担任中国银行业监督管理委员会培训中心主任，</font><font face=\"Times New Roman\">2006</font><font face=\"宋体\">年至今历任中国建银投资有限责任公司监事长、纪委书记。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
