 INSERT INTO `mu_article` VALUES ('1284','A股迎上涨高潮 幕后七大推手曝光',NULL,'4','17','44','A股迎上涨高潮 幕后七大推手曝光','<p>
	&nbsp;</p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">证券之星&nbsp;</span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">&nbsp; 周五<font face=\"Times New Roman\">A</font><font face=\"宋体\">股开盘丝毫不理会前夜美股大跌，小幅高开后一路狂奔，午后再度爆发第二轮上涨高潮。据证券之星独家探索，今日暴涨有以下原因：</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">1<font face=\"宋体\">、证监会外管局开闸产品申报，</font><font face=\"Times New Roman\">QFII</font><font face=\"宋体\">和</font><font face=\"Times New Roman\">RQFII</font><font face=\"宋体\">入市重提速，这无疑给饱受</font><font face=\"Times New Roman\">IPO</font><font face=\"宋体\">摧残的</font><font face=\"Times New Roman\">A</font><font face=\"宋体\">股雪中送炭。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">2<font face=\"宋体\">、银监会要求银行信贷重点支持扩内需，内需现在就是所有环节当中最重要的一环，政策开始吹暖风，股市望再现&ldquo;通胀式&rdquo;上涨。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">3<font face=\"宋体\">、传证监会密会摩根斯坦利，</font><font face=\"Times New Roman\">MSCI</font><font face=\"宋体\">新兴市场指数加入</font><font face=\"Times New Roman\">A</font><font face=\"宋体\">股权重正式沟通。尽管带一个&ldquo;传&rdquo;字，但市场要反弹，任何消息都有可能成导火索。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">4<font face=\"宋体\">、迄今为止，已有</font><font face=\"Times New Roman\">159</font><font face=\"宋体\">只个股年内已经跌破</font><font face=\"Times New Roman\">1949</font><font face=\"宋体\">点时的价位，股价已经充分反应了利空消息。大量个股杀破之前的底线价位，是市场阶段性见底的重要特征。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">6<font face=\"宋体\">、人民币兑美元汇率连续四天创历史新高，资金流入速度加快，股市增加资金活水。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
	<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">7<font face=\"宋体\">、国际金价暴跌后迎来强劲反弹，今日午后金价再度起航，</font><font face=\"Times New Roman\">A</font><font face=\"宋体\">股中的有色、资源股大幅反弹，成大盘暴涨的有力功臣。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'Times New Roman\'; \"><o:p></o:p></span></p>
','1','A股上涨,幕后推手','115','meif','2013-04-20 17:38:57',NULL,NULL,NULL);/* MySQLReback Separation */
