 INSERT INTO `mu_article` VALUES ('1291','我国加强钢铁行业协同创新','网络','4','17','42','我国加强钢铁行业协同创新','<p>
	&nbsp;</p>
<div class=\"Section0\">
	<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
		&nbsp; &nbsp;&nbsp;<span style=\"font-family: 宋体; font-size: 10.5pt;\">为了加强我国钢铁行业协同创新，多所高校、科研院所参与的钢铁共性技术协同创新中心已在基础研究、人才培养等多方面取得一系列成果。</span></p>
	<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
		<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
		<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">&nbsp; &nbsp;&ldquo;<font face=\"Times New Roman\">2011</font><font face=\"宋体\">计划&rdquo;钢铁共性技术协同创新中心现场考察验收会</font><font face=\"Times New Roman\">24</font><font face=\"宋体\">日在北京科技大学举行。据中心有关负责人介绍，钢铁工业的发展亟须在关键共性技术创新上突破。然而，钢铁行业创新体系尚未完善，企业间技术壁垒严重，企业与高校、院所间的合作独立分散，人才匮乏也严重制约着企业自主创新。实现我国钢铁工业从大到强的转型，从量到质的转变，必须&ldquo;政产学研用&rdquo;紧密结合，建立钢铁行业协同创新体系。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
		<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">
		<span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'宋体\'; \">&nbsp; &nbsp; 据介绍，<font face=\"Times New Roman\">2012</font><font face=\"宋体\">年，以北京科技大学和东北大学这两所冶金专业特色鲜明的高校为依托，联合上海大学等高校、中科院金属研究所等研究院所，以及鞍钢、宝钢等大型企业，共同组建了钢铁共性技术协同创新中心，目前已初步实现了协同体内基地、人才、设备、成果、信息等资源的整合。&ldquo;十二五&rdquo;启动的钢铁领域国家重大重点项目中，中心各单位承担任务比例超过</font><font face=\"Times New Roman\">70%</font><font face=\"宋体\">。中心的</font><font face=\"Times New Roman\">8</font><font face=\"宋体\">个研究团队围绕重大任务开展协同攻关，在多领域获得突出成果，已获国家级奖励</font><font face=\"Times New Roman\">9</font><font face=\"宋体\">项，省部级奖励</font><font face=\"Times New Roman\">20</font><font face=\"宋体\">项，申请专利</font><font face=\"Times New Roman\">583</font><font face=\"宋体\">项，发表论文</font><font face=\"Times New Roman\">579</font><font face=\"宋体\">篇。</font></span><span style=\"mso-spacerun:\'yes\'; font-size:10.5000pt; font-family:\'Times New Roman\'; \"><o:p></o:p></span></p>
</div>
','1','我国加强钢铁行业协同创新','115','meif','2013-04-22 09:44:39',NULL,NULL,NULL);/* MySQLReback Separation */
