 INSERT INTO `mu_article` VALUES ('1339','钼丝储存注意事项','网络','0','20','67','钼丝储存注意事项','<div class=\"Section0\" style=\"layout-grid: both loose 15.6pt none;\">
	<p class=\"p0\" style=\"margin-top: 0pt; margin-bottom: 0pt;\">
		<span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\">将钼丝保管于湿度较低的通风阴凉场所，温度请尽量控制在<font face=\"Times New Roman\">20</font><font face=\"宋体\">℃～</font><font face=\"Times New Roman\">25</font><font face=\"宋体\">℃，以避免钼丝发生氧化；</font></span><span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-top: 0pt; margin-bottom: 0pt;\">
		<span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\">钼丝放置时间超过<font face=\"Times New Roman\">6</font><font face=\"宋体\">个月可能会发生垂直度不良问题。</font></span><span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-top: 0pt; margin-bottom: 0pt;\">
		<span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\">钼丝的优点：</span><span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-top: 0pt; margin-bottom: 0pt;\">
		<span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\">钼丝精度高，&nbsp;线径公差控制在<font face=\"Times New Roman\">0</font><font face=\"宋体\">～－</font><font face=\"Times New Roman\">0.002mm</font><font face=\"宋体\">以内；</font></span><span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-top: 0pt; margin-bottom: 0pt;\">
		<span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\">断线发生率低，加工速率高，具有高性价比；</span><span style=\"font-family: \"宋体\"; font-size: 10.5pt; mso-spacerun: \"yes\";\"><o:p></o:p></span></p>
	<p class=\"p0\" style=\"margin-top: 0pt; margin-bottom: 0pt;\">
